import { useState } from 'react';

const QuestionItem = ({ question }) => {
    const {
        questiontext,
        varname,
    } = question

    const [selected, setSelected] = useState(-1);

    options = [
        question.option1,
        question.option2,
        question.option3,
        question.option4,
        question.option5,
        question.option6,
        question.option7,
    ];


    const handleOptionClick = (event) => {
        selected_option = Number(event.target.getAttribute('data-id'));
        console.log({ selected: selected_option });
        setSelected(selected_option);
    }

    return (
        <div className="question-item">
            <p className="question-description">{questiontext}</p>
            {
                options.map((option, index) => <span data-id={index} className={`question-option ${selected == index ? 'selected' : ''}`} onClick={handleOptionClick}>{option}</span>)
            }
        </div>
    )
};

export default QuestionItem
